import { Box, Typography } from "@mui/material";
import TextField from "@mui/material/TextField";
import { PasswordInput, PasswordChecklist } from "react-password-checklist";

const Payment = ({
  values,
  touched,
  errors,
  handleBlur,
  handleChange
}) => {
  return (
    <Box m="30px 0">
      {/* CONTACT INFO */}
      <Box>
        <Typography sx={{ mb: "15px" }} fontSize="18px">
          Create Account
        </Typography>
        <TextField
          fullWidth
          type="text"
          label="Email"
          onBlur={handleBlur}
          onChange={handleChange}
          value={values.email}
          name="email"
          error={!!touched.email && !!errors.email}
          helperText={touched.email && errors.email}
          sx={{ gridColumn: "span 4", marginBottom: "15px" }}
        />
        <TextField
          fullWidth
          type="text"
          label="Phone Number"
          onBlur={handleBlur}
          onChange={handleChange}
          value={values.phoneNumber}
          name="phoneNumber"
          error={!!touched.phoneNumber && !!errors.phoneNumber}
          helperText={touched.phoneNumber && errors.phoneNumber}
          sx={{ gridColumn: "span 4" }}
        />
        <PasswordChecklist value={values.password}>
          {(isValid, message) => (
            <PasswordInput
              fullWidth
              label="Password"
              value={values.password}
              name="password"
              onChange={handleChange}
              onBlur={handleBlur}
              error={!!touched.password && !!errors.password}
              helperText={touched.password && errors.password}
              sx={{ gridColumn: "span 4", marginBottom: "15px" }}
              isValid={isValid}
              message={message}
            />
          )}
        </PasswordChecklist>
        <PasswordInput
          fullWidth
          label="Verify Password"
          value={values.verifyPassword}
          name="verifyPassword"
          onChange={handleChange}
          onBlur={handleBlur}
          error={!!touched.verifyPassword && !!errors.verifyPassword}
          helperText={touched.verifyPassword && errors.verifyPassword}
          sx={{ gridColumn: "span 4", marginBottom: "15px" }}
        />
      </Box>
    </Box>
  );
};

export default Payment;
