import { Box, Button, IconButton, Typography } from "@mui/material";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Order from "../../components/Order";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import { shades } from "../../theme";
import { addToCart } from "../../state";
import { useDispatch } from "react-redux";
import Select from 'react-select';

const InvoiceDetails = () => {
  const dispatch = useDispatch();
  const { orderId } = useParams();
  const [value, setValue] = useState("description");
  const [count, setCount] = useState(1);
  const [order, setOrder] = useState(null);
  const [orders, setOrders] = useState([]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

const options = [
{ value: 'Men3', label: 'UK/AUST Mens 3/Ladies 5'},
{ value: 'Men4', label: 'UK/AUST Mens 4/Ladies 6'},
{ value: 'Men5', label: 'UK/AUST Mens 5/Ladies 7'},
{ value: 'Men6', label: 'UK/AUST Mens 6/Ladies 8'},
{ value: 'Men7', label: 'UK/AUST Mens 7/Ladies 9'},
{ value: 'Men8', label: 'UK/AUST Mens 8/Ladies 10'},
{ value: 'Men9', label: 'UK/AUST Mens 9'},
{ value: 'Men10', label: 'UK/AUST Mens 10'},
{ value: 'Men11', label: 'UK/AUST Mens 11'},
{ value: 'Men12', label: 'UK/AUST Mens 12'},
{ value: 'Men13', label: 'UK/AUST Mens 13'},
{ value: 'Men14', label: 'UK/AUST Mens 14'},
{ value: 'Men15', label: 'UK/AUST Mens 15'}
]

  async function getOrder() {
    const order = await fetch(
      `http://localhost:1337/api/orders/${orderId}?populate=image`,
      {
        method: "GET",
      }
    );
    const orderJson = await order.json();
    setOrder(orderJson.data);
  }

  async function getOrders() {
    const orders = await fetch(
      `http://localhost:1337/api/orders?populate=image`,
      {
        method: "GET",
      }
    );
    const ordersJson = await orders.json();
    setOrders(ordersJson.data);
  }

  useEffect(() => {
    getOrder();
    getOrders();
  }, [orderId]); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <Box width="80%" m="80px auto">
      <Box display="flex" flexWrap="wrap" columnGap="40px">
        {/* IMAGES */}
        <Box flex="1 1 40%" mb="40px">
          <img
            alt={order?.name}
            width="100%"
            height="100%"
            src={`http://localhost:1337${order?.attributes?.image?.data?.attributes?.formats?.medium?.url}`}
            style={{ objectFit: "contain" }}
          />
        </Box>

        {/* ACTIONS */}
        <Box flex="1 1 50%" mb="40px">
          <Box display="flex" justifyContent="space-between">
            <Box>Home/Order</Box>
            <Box>Prev Next</Box>
          </Box>

          <Box m="65px 0 25px 0">
            <Typography variant="h3">{order?.attributes?.name}</Typography>
            <Typography>${order?.attributes?.price}</Typography>
            <Typography sx={{ mt: "20px" }}>
              {order?.attributes?.longDescription}
            </Typography>
          </Box>

          <Box m="65px 0 25px 0">
            <Typography varien="h3">Select Size:</Typography>
              <Box>
                 <Select value={options.value} options= { options } defaultValue={options[5]} />
              </Box>
          </Box>

          <Box display="flex" alignItems="center" minHeight="50px">
            <Box
              display="flex"
              alignItems="center"
              border={`1.5px solid ${shades.neutral[300]}`}
              mr="20px"
              p="2px 5px"
            >
              <IconButton onClick={() => setCount(Math.max(count - 1, 0))}>
                <RemoveIcon />
              </IconButton>
              <Typography sx={{ p: "0 5px" }}>{count}</Typography>
              <IconButton onClick={() => setCount(count + 1)}>
                <AddIcon />
              </IconButton>
            </Box>
            <Button
              sx={{
                backgroundColor: "#222222",
                color: "white",
                borderRadius: 0,
                minWidth: "150px",
                padding: "10px 40px",
              }}
              onClick={() => dispatch(addToCart({ order: { ...order, options, count } }))}
            >
              ADD TO CART
            </Button>
          </Box>
          <Box>
            <Box m="20px 0 5px 0" display="flex">
              <FavoriteBorderOutlinedIcon />
              <Typography sx={{ ml: "5px" }}>ADD TO WISHLIST</Typography>
            </Box>
            <Typography>CATEGORIES: {order?.attributes?.category}</Typography>
          </Box>
        </Box>
      </Box>

      {/* INFORMATION */}
      <Box m="20px 0">
        <Tabs value={value} onChange={handleChange}>
          <Tab label="DESCRIPTION" value="description" />
          <Tab label="REVIEWS" value="reviews" />
        </Tabs>
      </Box>
      <Box display="flex" flexWrap="wrap" gap="15px">
        {value === "description" && (
          <div>{order?.attributes?.longDescription}</div>
        )}
        {value === "reviews" && <div>reviews</div>}
      </Box>

      {/* RELATED ITEMS */}
      <Box mt="50px" width="100%">
        <Typography variant="h3" fontWeight="bold">
          Related Products
        </Typography>
        <Box
          mt="20px"
          display="flex"
          flexWrap="wrap"
          columnGap="1.33%"
          justifyContent="space-between"
        >
          {orders.slice(0, 4).map((order, i) => (
            <Order key={`${order.name}-${i}`} order={order} />
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default InvoiceDetails;
